p; &amp;nbsp; &amp;nbsp; /*2D页墙洞属性栏*/&lt;/p&gt;&lt;p&gt;$.get(&amp;#39;/bb/views/propbar/choseBD.html&amp;#39;), &amp;nbsp; &amp;nbsp; &amp;nbsp; /*波打线*/&lt;/p&gt;&lt;p&gt;$.get(&amp;#39;/bb/views/render/rander.html&amp;#39;), &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;/*房间渲染-人视觉*/&lt;/p&gt;&lt;p&gt;$.get(&amp;#39;/bb/views/leftbar/modelRoom.html&amp;#39;), &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;/*样板间*/&lt;/p&gt;&lt;p&gt;$.get(&amp;#39;/bb/views/welcome/searchBuilding.html&amp;#39;), &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;/*搜索户型*/&lt;/p&gt;&lt;p&gt;$.get(&amp;#39;/bb/views/welcome/myPlan.html&amp;#39;) &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;/*我的笔记*/&lt;/p&gt;&lt;p&gt;).done(function (choseWall, replaceDP, choseFloor, choseFloorArea, choseGoods, choseGroup, choseWallArea, choseWaistLine, choseParquet , roomHard , furniture , combine, chose2d ,choseHouseTop ,choseCeilingProperty ,choseDoorWindow2D ,choseRoom2D ,choseEdge2D ,choseWallCave2D ,choseBD,rander,modelRoom,searchBuilding,myPlan) {&lt;/p&gt;&lt;p&gt;var ids = [&amp;#39;tpl-choseWall&amp;#39;, &amp;#39;tpl-replaceDP&amp;#39;, &amp;#39;tpl-choseFloor&amp;#39;, &amp;#39;tpl-choseFloorArea&amp;#39;, &amp;#39;tpl-choseGoods&amp;#39;, &amp;#39;tpl-choseGroup&amp;#39;, &amp;#39;tpl-choseWallArea&amp;#39;, &amp;#39;tpl-choseWaistLine&amp;#39;, &amp;#39;tpl-choseParquet&amp;#39;, &amp;#39;tpl-roomHard&amp;#39;, &amp;#39;tpl-furniture&amp;#39;, &amp;#39;tpl-combine&amp;#39;, &amp;#39;tpl-chose2d&amp;#39;, &amp;#39;tpl-choseHouseTop&amp;#39;, &amp;#39;tpl-choseCeilingProperty&amp;#39;, &amp;#39;tpl-choseDoorWindow2D&amp;#39;, &amp;#39;tpl-choseRoom2D&amp;#39;, &amp;#39;tpl-choseEdge2D&amp;#39;, &amp;#39;tpl-choseWallCave2D&amp;#39;, &amp;#39;tpl-choseBD&amp;#39;, &amp;#39;tpl-rander&amp;#39;, &amp;#39;tpl-modelRoom&amp;#39;,&amp;#39;tpl-searchBuilding&amp;#39;,&amp;#39;tpl-myPlan&amp;#39;];&lt;/p&gt;&lt;p&gt;for (var i = 0, max = arguments.length; i &amp;lt; max; i++) {&lt;/p&gt;&lt;p&gt;$(&amp;#39;body&amp;#39;).append(&amp;#39;&amp;lt;script id=&amp;quot;&amp;#39; + ids[i] + &amp;#39;&amp;quot; type=&amp;quot;text/template&amp;quot;&amp;gt;&amp;#39; + arguments[i][0] + &amp;#39;&amp;lt;/script&amp;gt;&amp;#39;);&lt;/p&gt;&lt;p&gt;}&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;});&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://lib.csdn.net/base/jquery&quot;&gt;jQuery&lt;/a&gt;的属性选择器分以下几种用法。&lt;/p&gt;&lt;p&gt;$(&amp;quot;div[id]&amp;quot;) &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;查找所有含有 id 属性的div元素。&lt;/p&gt;&lt;p&gt;$(&amp;quot;div[id=&amp;#39;ajaxa&amp;#39;]&amp;quot;) &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;查找 id 为 ajaxa 的 div 元素。&lt;/p&gt;&lt;p&gt;$(&amp;quot;div[id!=&amp;#39;ajaxa&amp;#39;]&amp;quot;) &amp;nbsp; &amp;nbsp; &amp;nbsp; 查找 id 不为 ajaxa 的 div 元素。&lt;/p&gt;&lt;p&gt;$(&amp;quot;div[id^=&amp;#39;ajaxa&amp;#39;]&amp;quot;) &amp;nbsp; &amp;nbsp; &amp;nbsp; 查找 id 以 ajaxa 开头的 div 元素。&lt;/p&gt;&lt;p&gt;$(&amp;quot;div[id$=&amp;#39;ajaxa&amp;#39;]&amp;quot;) &amp;nbsp; &amp;nbsp; &amp;nbsp; 查找 id 以 ajaxa 结尾的 div 元素。&lt;/p&gt;&lt;p&gt;$(&amp;quot;div[id*=&amp;#39;ajaxa&amp;#39;]&amp;quot;) &amp;nbsp; &amp;nbsp; &amp;nbsp; 查找 id 含 ajaxa 的 div 元素。&lt;/p&gt;&lt;p&gt;$(&amp;quot;div[id^=&amp;#39;ajaxa&amp;#39;]&amp;quot;) &amp;nbsp; &amp;nbsp; &amp;nbsp; 查找 id 以 ajaxa 开头的 div 元素。&lt;/p&gt;&lt;p&gt;$(&amp;quot;div[id][class=&amp;#39;ajaxa&amp;#39;]&amp;quot;) 查找存在id属性并且类为ajaxa的 div 元素。&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;','2018-03-28 16:05:08','2019-12-14 13:04:30','1'),
	(739,1,22,'express 搭建','&lt;p&gt;一、安装Node.js&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;1.进入&lt;a href=&quot;http://nodejs.org/&quot;&gt;Node.js官网&lt;/a&gt;下载并安装&lt;/p&gt;&lt;p&gt;&lt;img data-fr-image-pasted=&quot;true&quot; alt=&quot;&quot; data-media-type=&quot;image&quot; src=&quot;http://images.cnitblog.com/blog/554327/201411/191116384376074.jpg&quot; class=&quot;fr-fic fr-dii&quot;&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;2.启动cmd输入命令查看node及npm版本&lt;/p&gt;&lt;p&gt;node -vnpm -v&lt;/p&gt;&lt;p&gt;&lt;img data-fr-image-pasted=&quot;true&quot; alt=&quot;&quot; data-media-type=&quot;image&quot; src=&quot;http://images.cnitblog.com/blog/554327/201411/191117008282701.jpg&quot; class=&quot;fr-fic fr-dii&quot;&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://www.npmjs.org/&quot;&gt;npm&lt;/a&gt;是node完成安装后自带的包管理器&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;二、安装Express&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://expressjs.com/&quot;&gt;express&lt;/a&gt;是node平台下最常用的web应用开发框架&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;1.全局安装express&lt;/p&gt;&lt;p&gt;npm install -g express&lt;/p&gt;&lt;p&gt;Win7环境全局安装在C:\\Users\\Administrator\\AppData\\Roaming\\npm\\node_modules文件夹下&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;2.全局安装express命令行工具&lt;/p&gt;&lt;p&gt;npm install -g express-generator&lt;/p&gt;&lt;p&gt;在最新的4.x版本中，命令行工具已经被独立出来&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;3.查看express版本&lt;/p&gt;&lt;p&gt;express -V&lt;/p&gt;&lt;p&gt;&lt;img data-fr-image-pasted=&quot;true&quot; alt=&quot;&quot; data-media-type=&quot;image&quot; src=&quot;http://images.cnitblog.com/blog/554327/201411/191117240622969.jpg&quot; class=&quot;fr-fic fr-dii&quot;&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;4.更新已安装的express&lt;/p&gt;&lt;p&gt;npm update -g express&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;三、使用Express创建项目&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;1.创建项目&lt;/p&gt;&lt;p&gt;express APP_NAME&lt;/p&gt;&lt;p&gt;express默认以&lt;a href=&quot;http://jade-lang.com/&quot;&gt;jade&lt;/a&gt;作为模板引擎，模板文件为.jade类型；jade的语法与html有较大差异，对缩进也是要求严格的&lt;/p&gt;&lt;p&gt;&lt;img data-fr-image-pasted=&quot;true&quot; alt=&quot;&quot; data-media-type=&quot;image&quot; src=&quot;http://images.cnitblog.com/blog/554327/201411/201003162656575.jpg&quot; class=&quot;fr-fic fr-dii&quot;&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;2.以&lt;a href=&quot;http://www.embeddedjs.com/&quot;&gt;ejs&lt;/a&gt;作为模板引擎创建项目&lt;/p&gt;&lt;p&gt;express APP_NAME -e&lt;/p&gt;&lt;p&gt;在项目名称的后面添加 -e 那么创建出来的模板文件就是.ejs类型，语法是与html相同的&lt;/p&gt;&lt;p&gt;&lt;img data-fr-image-pasted=&quot;true&quot; alt=&quot;&quot; data-media-type=&quot;image&quot; src=&quot;http://images.cnitblog.com/blog/554327/201411/201004212965141.jpg&quot; class=&quot;fr-fic fr-dii&quot;&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;3.将ejs文件改为html&lt;/p&gt;&lt;p&gt;若希望模板文件的后缀为.html(通常只是为了看着更舒服)，那么在手动将.ejs换成.html后，还需要对app.js文件进行设置&lt;/p&gt;&lt;p&gt;var ejs = require(&amp;#39;ejs&amp;#39;);app.engine(&amp;#39;.html&amp;#39;, ejs.__express);app.set(&amp;#39;view engine&amp;#39;, &amp;#39;html&amp;#39;); &amp;nbsp; &amp;nbsp;//app.set(&amp;#39;view engine&amp;#39;, &amp;#39;ejs&amp;#39;);&lt;/p&gt;&lt;p&gt;原文件：&lt;/p&gt;&lt;p&gt;&lt;img data-fr-image-pasted=&quot;true&quot; alt=&quot;&quot; data-media-type=&quot;image&quot; src=&quot;http://images.cnitblog.com/blog/554327/201411/201010581874289.jpg&quot; class=&quot;fr-fic fr-dii&quot;&gt;&lt;/p&gt;&lt;p&gt;修改后：&lt;/p&gt;&lt;p&gt;&lt;img data-fr-image-pasted=&quot;true&quot; alt=&quot;&quot; data-media-type=&quot;image&quot; src=&quot;http://images.cnitblog.com/blog/554327/201411/201011112033281.jpg&quot; class=&quot;fr-fic fr-dii&quot;&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;4.安装依赖包&lt;/p&gt;&lt;p&gt;进入项目文件夹，执行：&lt;/p&gt;&lt;p&gt;npm install&lt;/p&gt;&lt;p&gt;下载package.json中记录的所有依赖包到node_modules文件夹&lt;/p&gt;&lt;p&gt;注意：更多npm命令，请查看&lt;a href=&quot;http://www.cnblogs.com/zhnoah/p/4011927.html&quot;&gt;npm常用命令集合&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;四、启动服务&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;1.启动&lt;/p&gt;&lt;p&gt;npm start&lt;/p&gt;&lt;p&gt;监听bin/www文件，默认端口为3000，可在www文件中更改&lt;/p&gt;&lt;p&gt;&lt;img data-fr-image-pasted=&quot;true&quot; alt=&quot;&quot; data-media-type=&quot;image&quot; src=&quot;http://images.cnitblog.com/blog/554327/201411/191127336254245.jpg&quot; class=&quot;fr-fic fr-dii&quot;&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;2.打开浏览器，输入localhost:3000&lt;/p&gt;&lt;p&gt;&lt;img data-fr-image-pasted=&quot;true&quot; alt=&quot;&quot; data-media-type=&quot;image&quot; src=&quot;http://images.cnitblog.com/blog/554327/201411/191129055002677.jpg&quot; class=&quot;fr-fic fr-dii&quot;&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;3.自动重启服务&lt;/p&gt;&lt;p&gt;打开package.json可以看见，监听bin/www文件的命令是node&lt;/p&gt;&lt;p&gt;&lt;img data-fr-image-pasted=&quot;true&quot; alt=&quot;&quot; data-media-type=&quot;image&quot; src=&quot;http://images.cnitblog.com/blog/554327/201411/191132122502024.jpg&quot; class=&quot;fr-fic fr-dii&quot;&gt;&lt;/p&gt;&lt;p&gt;使用node监听将会导致：每次修改代码后，需要手动重启服务，可以使用&lt;a href=&quot;https://www.npmjs.org/package/nodemon&quot;&gt;nodemon&lt;/a&gt;进行自动重启&lt;/p&gt;&lt;p&gt;npm install -g nodemon&lt;/p&gt;&lt;p&gt;同样可以使用下面的命令查看其版本&lt;/p&gt;&lt;p&gt;nodemon -v&lt;/p&gt;&lt;p&gt;将package.json文件中的代码修改为：&lt;/p&gt;&lt;p&gt;&amp;quot;start&amp;quot;: &amp;quot;nodemon ./bin/www&amp;quot;&lt;/p&gt;&lt;p&gt;重新执行：&lt;/p&gt;&lt;p&gt;npm start&lt;/p&gt;&lt;p&gt;&lt;img data-fr-image-pasted=&quot;true&quot; alt=&quot;&quot; data-media-type=&quot;image&quot; src=&quot;http://images.cnitblog.com/blog/554327/201411/191137463755246.jpg&quot; class=&quot;fr-fic fr-dii&quot;&gt;&lt;/p&gt;&lt;p&gt;接下来，就可以随心所欲的编码了...&lt;/p&gt;&lt;p&gt;#########################################&lt;/p&gt;&lt;p&gt;Node.js正在快速发展中，软件包升级很快，文章有运行不通的地方请参考官方文档解决。本人也会不定期更新文章，尽量保持文章代码的可用性。&lt;/p&gt;&lt;p&gt;#########################################&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;作者：@z