	(542,'golang模拟浏览器请求','https://www.mogublog.net/post/2081.html','import (\r\n	\"io/ioutil\"\r\n	\"net/http\"\r\n)\r\n\r\nfunc httpRequest(url string) []byte {\r\n	request, err := http.NewRequest(\"GET\", url, nil)\r\n	if err != nil {\r\n		return []byte{}\r\n	}\r\n\r\n	request.Header.Add(\"Accept\", \"text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,image/apng,*/*;q=0.8\")\r\n	request.Header.Add(\"Accept-Language\", \"zh-CN,zh;q=0.8,en-US;q=0.5,en;q=0.3\")\r\n	request.Header.Add(\"Connection\", \"keep-alive\")\r\n	request.Header.Add(\"User-Agent\", \"Mozilla/5.0 (Windows NT 6.1; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/68.0.3440.106 Safari/537.36\")\r\n\r\n	client := http.Client{}\r\n	response, err := client.Do(request)\r\n	if err != nil {\r\n		return []byte{}\r\n	}\r\n	defer response.Body.Close()\r\n\r\n	body, err := ioutil.ReadAll(response.Body)\r\n	if err != nil {\r\n		return []byte{}\r\n	}\r\n\r\n	return body\r\n}','2019-12-03 00:20:38',70),
